/* @flow */
/* @jsx jsxDom */

import { jsxDom } from 'xcomponent/src/lib';

export function buttonTemplate({ props } : { props : Object }) : HTMLElement {

    let currency = '';
    let amount = '';

    if (props.payment.currency === 'rai') {
        currency = '';

        function hasRai(num) {
            num = parseInt(num) / 1000;
            return num > Math.floor(num)
        }

        amount = hasRai(props.payment.amount)
            ? (parseInt(props.payment.amount) / 1000000).toFixed(6)
            : (parseInt(props.payment.amount) / 1000000).toFixed(3);
    }
    return (
        <div class="brainblocks-button-container">
            <style>
                {`
                    .brainblocks-button {
                        display: inline-block;
                        width: 100%;
                        height: 50px;
                        background-color: #eee;
                        border-radius: 5px;
                        font-family: Helvetica, Arial, sans-serif;
                        line-height: 50px;
                        color: #1A3238;
                        cursor: pointer;
                        font-size: 16px;
                        text-align: center;
                        letter-spacing: 1px;
                    }

                    .brainblocks-button-content {
                        width: 100%;
                        height: 50px;

                        display: flex;
                        flex-direction: row;
                        justify-content: center;
                        align-items: center;
                    }

                    .brainblocks-button .brainblocks-raiblocks-logo {
                        height: 15px;
                        margin-left: 10px;
                        border-left: 1px solid #ccc;
                        padding-left: 10px;
                    }

                    .brainblocks-button .brainblocks-raiblocks-logo-small {
                        height: 15px;
                        margin-left: 5px;
                        border-left: 1px solid #ccc;
                        padding-left: 5px;
                        display: none;
                    }

                    @media screen and (max-width: 299px) {
                        .brainblocks-button .pay-text {
                            display: none;
                        }
                    }

                    @media screen and (max-width: 200px) {
                        .brainblocks-button .brainblocks-raiblocks-logo {
                            display: none;
                        }

                        .brainblocks-button .brainblocks-raiblocks-logo-small {
                            display: inline-block;
                        }
                    }
                `}
            </style>

            <div role="button" class="brainblocks-button">
                <div class="brainblocks-button-content">
                    <span>
                        <span class="pay-text">{ amount && 'Pay ' }</span>
                        <span>
                            <span id="pay-amount" class="pay-amount">{ amount || <img class="loading-spinner" src="data:image/svg+xml;base64," /> }</span>
                            <span> </span>
                            <span id="pay-currency" class="pay-currency">{ currency }</span>
                        </span>
                    </span>
                    <img class="brainblocks-raiblocks-logo" src="data:image/svg+xml;base64," />
                    <img class="brainblocks-raiblocks-logo-small" src="data:image/svg+xml;base64," />
                </div>
            </div>
        </div>
    );
}

